import React, {Component} from 'react';

import '../styles/navigation.css'
import '../styles/tables.css'
import {getPosts, getTopPosts} from  '../services/posts' 


class Posts extends Component {
    constructor(){
      super()
      this.state = {
        posts: '',
        links: '',
      }
      this.fetchTopPosts = this.fetchTopPosts.bind(this);
      this.fetchPosts = this.fetchPosts.bind(this);
    }

    async fetchPosts() {
        const {data, links} = await getPosts(localStorage.getItem('token'));
        this.setState({ posts: Object.values(data), links: links});
      }
    async fetchTopPosts() {
        const {data, links} = await getTopPosts(localStorage.getItem('token'));
        this.setState({ posts: Object.values(data), links: links});
      }


    render() {
      let posts;
      if (this.state.posts){
        posts = this.state.posts.map((post, i) => {
          return (
            <tbody  key= {i}>
              <tr>
                <td>{post.links.freelancer}</td>
                <td>{post.attributes.description}</td>
                <td><img className='table-img' src={post.attributes.media} alt="" /></td>
                <td>{post.attributes.likes}</td>
                <td>{post.links.comments}</td>
              </tr>
            </tbody>
          );    
        });
      }

      return (
        <div className='container'>
          <h2>Posts</h2>

          <div>
          <ul className="list-group list-group-horizontal-sm">
            <li className="">
              <button onClick={this.fetchTopPosts}> Top 10 (Likes)</button>
            </li>
            <li className="">
              <button onClick={this.fetchPosts}> Todos los Posts</button>
              </li>
          </ul>
        </div>


          <table>
            <thead>
            <tr>
              <th>Freelancer</th>
              <th>Description</th>
              <th>Media</th>
              <th>Likes</th>
              <th>Comentarios</th>
            </tr>
            </thead>
            {posts}
          </table>
        </div>
      );
    }
  }


  export default Posts;
