class App extends Component {
  constructor(){
    super()
    this.state = {
      user: '',
      token: '',
    }
  }

  render() {
    let login;
    if (this.state.user === '') {
      login =  <Login/> 

    }

    return (
      <div>

        <Navigation/>
        <Posts/>
        {login}


      </div>
    );
  }
}

export default App;
