import React, {Component} from 'react';

import '../styles/navigation.css'
import {getPosts} from  '../services/posts' 


class Posts extends Component {
    constructor(){
      super()
      this.state = {
        posts: '',
        links: '',
      }
    }

    async fetchPosts() {
        const {data, links} = await getPosts(localStorage.getItem('token'));
        await this.setState({ posts: Object.values(data), links: links});

      }

    componentDidMount(){
      this.fetchPosts()
    }

    render() {
      let posts;
      if (this.state.posts){
        posts = this.state.posts.map((post, i) => {
          return (
            <div className='card' key= {i}>
          <h2 >{post.attributes.description}</h2>
          <img src={post.attributes.media} alt="" />
          </div>

          );

        });

      }

      return (
        <table>
          <tr>
            <th>Company</th>
            <th>Contact</th>
            <th>Country</th>
          </tr>
          {posts}
        </table>
      );
    }
  }


  export default Posts;
