import React, {Component} from 'react';

//import './App.css';
//import './styles/navigation.css'
import logo from './logo.svg';
import Posts from './components/Posts.jsx'
import Login from './components/Login.jsx'


class App extends Component {
  constructor(){
    super()
    this.state = {
      token: '',
      status:''
    }
    this.logout = this.logout.bind(this);
    this.changeState = this.changeStatus.bind(this)
  }
  async componentDidMount(){
    this.setState({token: localStorage.getItem('token'), status: localStorage.getItem('status')})
  }

  async logout() {
    await localStorage.removeItem('token');
    window.location.reload(false)
  }

  async changeStatus(newStatus){
    await this.setState({status: newStatus})
    await localStorage.setItem('status', newStatus)
  }

  render() {
    let login;

    let logoutBtn;
    let posts;
    if (!this.state.token) {
      login =  <Login/>
    }   
    else {
      logoutBtn = <button className='nav-btn' onClick={this.logout}> LogOut</button> 
      if (this.state.status === 'posts'){
        posts = <Posts/>

      }
    }

    return (
      <div>

        <nav className='"navbar navbar-expand-lg navbar-dark bg-dark"'>
          <img src={logo} className="App-logo" alt="logo" />
          <button className='nav-btn' onClick={()=> this.changeStatus('posts')} >Posts</button>
          <button className='nav-btn' onClick={()=> this.changeStatus('freelancers')} >Freelancers</button>
          {logoutBtn}
          </nav>
        {posts}
        {login}

      </div>
    );
  }
}

export default App;
