async function getPosts(token) {
    const response = await fetch('http://localhost:3000/api/posts',{
    headers: {Authorization: `Bearer ${token}`} 
    });
    let {data, links} = await response.json()
    data = Object.values(data);
    data = await data.forEach(async (post, i)=>{
        post.attributes['freelancerUsername'] = await getFreelancerName(token ,Object.values(post.attributes)[1])
    });
    return {data, links}    
}

async function getTopPosts(token) {
    const response = await fetch('http://localhost:3000/api/posts/top',{
    headers: {Authorization: `Bearer ${token}`} 
    });
    let {data, links} = await response.json()
    data = await Object.values(data);
    const hola  = await data.forEach((post)=>{
        post.attributes['freelancerUsername'] = getFreelancerName(token ,Object.values(post.attributes)[1])

    });


    return {data, links}
}

async function getComments(token, postId) {
    const response = await fetch(`http://localhost:3000/api/posts/comments/${postId}`,{
    headers: {Authorization: `Bearer ${token}`} 
    });
    return response.json();
}

async function getFreelancerName(token, freelancerId) {
    let response = await fetch(`http://localhost:3000/api/freelancers/show/${freelancerId}`,{
    headers: {Authorization: `Bearer ${token}`} 
    });
    let {data} = await response.json().then();
    const username = Object.values(data.attributes)[3]
    return username

}

export {getPosts, getTopPosts, getComments, getFreelancerName};
